import { createSlice } from "@reduxjs/toolkit";

export const topicsSlice = createSlice({
    name: 'topics',
    initialState: {
        //will hold all topics like this '123':{id:'', name:'' icon:'',auizIds:[] },
        topics:  {},
    },
    reducer: {
        addTopic: (state, action) => {
            //the payload for this action to look like 
            //{id: '123456', name: 'name of topic', icon: 'icon url'}. 
            //Store these values in the state as a new topic object.
            const { id, name, icon } = action.payload;
            state.topics[id] = { id, name, icon, quizIds: [] };
        },
    },
});

export const selectTopics = (state) => state.topics.topics;
export const { addTopic } = topicsSlice.actions;
export default topicsSlice.reducer;
