import { createSlice } from "@reduxjs/toolkit";

export const topicsSlice = createSlice({
    name: 'topics',
    initialState: {
        //will hold all topics like this {'123':{id:'', name:'' icon:'',auizIds:[] },}
        topics:  {},
    },
    reducers: {
        addTopic: (state, action) => {
            const { id, name, icon } = action.payload;
            state.topics[id] = { id, name, icon, quizIds: [] };
        },
        addQuizzId: (state, action) =>{
            //payload: { id: '123', name: 'quiz name', topicId: '456', cardIds: ['1', '2', '3', ...]}
            //add the quiz id to quizIds array of the associated topic 
            const {topicId, id} = action.payload;
            state.topics[topicId].quizIds.push(id)
        }
    },
    extraReducers: (builder) => {
        builder.addCase(addQuiz, (state, action) => {
          // Find the correct topic by topicId
          const { topicId, quizId } = action.payload;
          const topic = state.find((topic) => topic.id === topicId);

          if (topic) {
            // Push the quizId into the topic's quizIds array
            topic.quizIds.push(quizId);
          }
        });
      },
});

export const selectTopics = (state) => state.topics.topics;
export const { addTopic, addQuizzId } = topicsSlice.actions;
export default topicsSlice.reducer;
